div.script:
(function($){
  $(function() {
    if($('.top-bangla-news-list').length) {

      var currentdate = new Date();
          var datetime = currentdate.getFullYear() + "-"
                  + (currentdate.getMonth()+1)  + "-"
                  + currentdate.getDate() + "-"
                  + currentdate.getHours() + "-";

      var minute = currentdate.getMinutes();
      minute = minute  - parseInt( minute % 5 );
      var time_str = datetime + minute;

      $.get('/bangla/json/newsList?catId=377&count=3&time=' + time_str, function( data ) {
        var html = '';
         var cat_name =  "শীর্ষ খবর";
        for(var i in data['newslist']) {
          if(i == 0) {
            html += '
';           
            var img_tag = ''; 

           // if(data['newslist'][i].byline != "স্টার অনলাইন রিপোর"){
              //  cat_name = "";
           // } 

          if(typeof data['newslist'][i].images[0].img_url != "undefined") {
              img_tag = '';
            }
            html += '
' + img_tag + '';
            //html += '
' +  cat_name + '';           

             html += '
' + data['newslist'][i].headline + '';    
           /*  html += '
' + data['newslist'][i].news_excerpt + '';   */
          } else {
            html += '
';
            html += '
' + data['newslist'][i].headline + '';
          } 
          html += '';
        }
        $('.top-bangla-news-list').html(html);
      }, 'json' );
    }
  });
})(jQuery);
